import { OffChainSignType } from 'src/types';
import { WalletContextState } from '@solana/wallet-adapter-react';
import { Keypair } from '@solana/web3.js';
export enum SolanaChains {
  devnet = 'devnet',
  testnet = 'testnet',
  mainnet = 'mainnet-beta',
}

